import React, { useEffect, useState } from "react";
import axios from "axios";
import Loader from "../components/Loader";

const Offers = () => {
  const [offers, setOffers] = useState();
  const [loading, setisLoading] = useState(true);

  const getData = async () => {
    const req = await axios.get(
      `https://api.adzuna.com/v1/api/jobs/fr/search/3?app_id=a8d2c594&app_key=16b7abd53d888e1013c9c8735e92b9af&title_only=backend`,
    );

    const res = await req.data;
    if (res) {
      setisLoading(false);
      setOffers(res.results);
      console.log(offers)
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (loading?<Loader/> :
    <div className="min-h-screen m-4 sm:w-2/3 mx-auto  flex flex-col justify-items-center">
      <div className="flex flex-col items-center text-center justify-items-center">
         {offers?.map(item => (
           <div className="flex flex-col p-2 m-3 border-2 ">
             <h1 className="text-3xl p-2 font-bold">{item.company.display_name}</h1>
             <h2 className="text-xl p-2">{item.title}</h2>
             <h3>{item.description}</h3>
              <h3><a href={item.redirect_url}>{item.redirect_url}</a></h3>
           </div>
         ))}
      </div>
    </div>
  );
};

export default Offers;
