import React, { useState, useEffect } from "react";
import Loader from "./Loader";

const Rexplorer = ({ request }) => {
  const [isLoading, setisLoading] = useState(true);
  const [data, setData] = useState();

  const provideData = (donn) => {
    setData(donn.result);
    console.log(donn.result);
  };

  useEffect(() => {
    if (request) {
      provideData(request);
      setisLoading(false);
    }
  }, [request]);

  return isLoading ? (
    <Loader />
  ) : (
    <div className="w-full"> 
      <table class="relative w-full bg-yellow-800 mx-auto sm:w-2/3">
        <thead className="border-2" >
          <tr className="justify-center">
            <th className="border-2 p-2">id_demande</th>
            <th className="border-2 p-2">Nom</th>
            <th className="border-2 p-2">Prenom</th>
            <th className="border-2 p-2">Confirmer</th>
            <th className="border-2 p-2">Refuser</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr className="flex  border-2 ">
              <td className="text-center" key="">
                {item.id_demande}
              </td>
              <td className="text-center " key="">
                {item.user_name}
              </td>
              <td className="text-center " key="">
                {item.user_sirname}
              </td>
              <td className="text-center " key="">
                {" "}
                <button className="bg-green-500 p-2 cursor-pointer hover:bg-green-300">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    class="h-6 w-6"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    <path
                      stroke-linecap="round"
                      stroke-linejoin="round"
                      stroke-width="2"
                      d="M5 13l4 4L19 7"
                    />
                  </svg>
                </button>
              </td>
              <td className="text-center " key="">
                {" "}
                <button className="bg-red-500 p-2 cursor-pointer hover:bg-red-300">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    class="h-6 w-6"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    <path
                      stroke-linecap="round"
                      stroke-linejoin="round"
                      stroke-width="2"
                      d="M6 18L18 6M6 6l12 12"
                    />
                  </svg>
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Rexplorer;
