import { createSlice } from "@reduxjs/toolkit";






const loginSlice = createSlice({
    name: "loginstatus",
    initialState: {value: false},
    reducers: {
        getsituation: (state, action) =>{
            state.value = action.payload;
        }
    }
})



export default loginSlice.reducer;
