import React,{useState, useEffect} from 'react'
import Loader from './Loader'

const Rexplorer = ({request}) => {
    const [isLoading, setisLoading] = useState(true)
    const [data, setData] = useState()


    const provideData = (donn) => {
        setData(donn.result)
        console.log(donn.result)
    }



    useEffect(() =>{
         if(request){
             provideData(request)
             setisLoading(false)
         }
    }, [request])


    return (
        isLoading ? <Loader/> : 
        <div class="p-10">
        <table class="table-auto">
        <thead>
          <tr className="border-2 p-2">
            <th>id_demande</th>
            <th>Nom</th>
            <th>Prenom</th>
            <th>Confirmer</th>
          </tr>
        </thead>
        <tbody>
         {data.map((item) => 
         <tr className="border-2 ">
             <td className="text-center" key="">{item.id_demande}</td>
             <td className="text-center "  key="">{item.user_name}</td>
             <td className="text-center "  key="">{item.user_sirname}</td>
             <td className="text-center "  key=""> <button className="bg-green-500 p-2">Confirmer</button></td>
         </tr>


            )}
        </tbody>
      </table>
      </div>
    )
}

export default Rexplorer
