import axios from 'axios';
import React, {useState} from 'react'

const Upload = () => {

    const [filename, setFilename] = useState();
    const [file, setFile] = useState();



    const getFile = e =>{
       setFilename(e.target.files[0].name)

       setFile(e.target.files[0])

    }


    const submitForm =  (e) =>{
      e.preventDefault();
      const formData = new FormData();

//formData.append("name", filename)
      formData.append("subject", file)


    //console.log(file)

     try {
         axios.post('http://localhost:5000/api/user/upload', formData).then(res => console.log(res)).catch((err) =>console.log(err));
     } catch (error) {
         if(error.status === 500){
             console.log('there was a problem uploading the file')
         }

     }

    }

    return (
        <div className="h-screen p-4">
            <form onSubmit={submitForm} className="relative flex flex-col place-items-center justify-center  h-2/3 inset-y-1/2 transform -translate-y-1/2">
                 <input  className="p-2 w-full cursor-pointer text-white border-2 border-dotted border-gray-300 h-1/2"  type="file" accept=".pdf"  name="subject"  onChange={getFile}/>
                 <label htmlFor='filelabel'>{filename}</label>
                 <input className="p-3 cursor-pointer w-full m-3 hover:bg-gray-400" type="submit"  value="Upload" />
            </form>
        </div>
    )
}

export default Upload
