    const getFile = e =>{

       setFile(e.target.files[0].name)
    }

    return (
        <div className="h-screen p-4">
            <form className="relative flex flex-col place-items-center justify-center  h-2/3 inset-y-1/2 transform -translate-y-1/2">
                 <input  className="p-2 w-full cursor-pointer text-white border-2 border-dotted border-gray-300 h-1/2" id='filelable' type="file"  name="file"  onChange={getFile}/>
                 <label htmlFor='filelabel'>{file}</label>
                 <input className="p-3 cursor-pointer w-full m-3 hover:bg-gray-400" type="submit"  value="Upload" />
            </form>
        </div>
    )
}

export default Upload
