import React, { useEffect, useState } from "react";
import axios from "axios";
import Loader from "../components/Loader";

const Offers = () => {
  const [offers, setOffers] = useState();
  const [loading, setisLoading] = useState(true);

  const getData = async () => {
    const req = await axios.get(
      `https://job-search4.p.rapidapi.com/indeed/search?query=developpeur&page=2`,
      {
        headers: {
          "X-RapidAPI-Host": "job-search4.p.rapidapi.com",
          "X-RapidAPI-Key":
            "385d803c34msh4f0c8826e915ee8p1137f6jsn3639c58a6825",
        },
      }
    );

    const res = await req.data;
    if (res) {
      setisLoading(false);
      setOffers(res.jobs);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="min-h-screen m-4 sm:w-2/3 mx-auto  flex flex-col text-center justify-items-center">
      <div className="flex items-center justify-items-center">
        {offers?.map((item) => (
          <div className="p-3 m-2 border-2 ">
            <h1 className="p-1 text-2xl font-bold">{item.title}</h1>
            <h1 className="text-blue-900 text-2xl">{item.company_name}</h1>
            <h3>{item.description}</h3>
            <h3 className="p-3">Pays: {item.country}</h3>
            <h3>
              {" "}
              Lien:{" "}
              <a className="text-green-500" href={item.detail_url}>
                {item.detail_url}
              </a>
            </h3>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Offers;
