import axios from "axios";
import React, { useState } from "react";

const Upload = () => {
  const [filename, setFilename] = useState();
  const [file, setFile] = useState();

  const getFile = (e) => {
    setFilename(e.target.files[0].name);

    setFile(e.target.files[0]);
  };

  const submitForm = (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("name", filename);
    formData.append("subject", file);

    console.log(file);

    try {
      axios
        .post("http://localhost:5000/api/user/upload", formData)
        .then((res) => console.log(res))
        .catch((err) => console.log(err));
    } catch (error) {
      if (error.status === 500) {
        console.log("there was a problem uploading the file");
      }
    }
  };

  return (
    <div className="h-screen p-4">
      <form className=" h-screeen flex flex-col bg-green-300" onSubmit={submitForm}>


         <div className="flex flex-col">
            <label htmlFor="domaine">Niveau d'etude:</label>
            <select className="p-2" id="year" name="year">
              <option value="1">License 1</option>
              <option value="2">License 2</option>
              <option value="3">License 3</option>
              <option value="4">Master 1</option>
              <option value="5">Master 2</option>
            </select>
         </div>




        <div className="relative flex flex-col">
          <input
            className="p-2 w-full cursor-pointer text-white border-2 border-dotted border-gray-300"
            type="file"
            accept=".pdf"
            name="subject"
            onChange={getFile}
          />
          <label htmlFor="filelabel">{filename}</label>
          <input
            className="p-3 cursor-pointer w-full m-3 hover:bg-gray-400"
            type="submit"
            value="Upload"
          />
        </div>
      </form>
    </div>
  );
};

export default Upload;
