import React, { useEffect, useState } from "react";
import axios from "axios";
import Loader from "../components/Loader";

const Offers = () => {
  const [offers, setOffers] = useState();
  const [loading, setisLoading] = useState(true);

  const getData = async () => {
    const req = await axios.get(
      `https://api.adzuna.com/v1/api/jobs/fr/search/1?app_id=a8d2c594&app_key=16b7abd53d888e1013c9c8735e92b9af&title_only=frontend`,
    );

    const res = await req.data;
    if (res) {
      setisLoading(false);
      setOffers(res.results);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="min-h-screen m-4 sm:w-2/3 mx-auto  flex flex-col text-center justify-items-center">
      <div className="flex items-center justify-items-center">
       <Loader/>
         {offers?.map(item => (
           <h1>item.company.display_name</h1>
         ))}
      </div>
    </div>
  );
};

export default Offers;
